using UnityEngine.Networking;
using System.Security.Cryptography.X509Certificates;

public class RestCertificateHandler : CertificateHandler {

    private static string PUB_KEY = "";
    protected override bool ValidateCertificate(byte[] cert) {
        return new X509Certificate2(cert).GetPublicKeyString().Equals(PUB_KEY);
    }
}